import React, { createContext, useState, useEffect } from "react";
import swal from "sweetalert";
import axios from "axios";

const baseUrl = "http://localhost:8080/api";

export const AppContext = createContext();

export const ContextProvider = ({ children }) => {
  const token = sessionStorage.getItem("token");
  const [selectedCategory, setSelectedCategory] = useState([]);
  const [productList, setProductList] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState([]);
  const [loading, setLoading] = useState(false);
  const [categoryList, setCategoryList] = useState([]);
  const [currentUser, setCurrentUser] = useState();
  const [drop, setDrop] = useState("");
  const [cart, setCart] = useState([]);
  const [purchased, setPurchased] = useState();
  const [showCheckoutModal, setShowCheckoutModal] = useState(false);
  let storage = JSON.parse(localStorage.getItem("guestCart"));
  const guestCart = {
    isOpen: true,
    products: [],
    cartQuantity: 0,
    cartTotal: 0,
  };

  //MODAL SWITCHES//
  //LogIn Modal Switches
  const handleLoginModalOpen = () => {
    setLogInModalShow(true);
  };
  const handleLoginModalClose = () => {
    setLogInModalShow(false);
  };
  const handleLoginModalSwitch = () => {
    setLogInModalShow(false);
    setSignUpModalShow(true);
  };

  //SignUp Modal Show
  const handleSignupModalOpen = () => {
    setSignUpModalShow(true);
  };
  const handleSignupModalClose = () => {
    setSignUpModalShow(false);
  };
  const handleSignupModalSwitch = () => {
    setSignUpModalShow(false);
    setLogInModalShow(true);
  };

  //Get list of categories
  const getCategoryList = async () => {
    axios
      .get(`${baseUrl}/categories`)
      .then((res) => {
        setCategoryList(res.data);
      })
      .catch((err) => swal(err.message));
  };

  //Get product list
  const getProductList = async () => {
    axios
      .get(`${baseUrl}/product/list`)
      .then((res) => {
        setProductList(res.data);
      })
      .catch((err) => swal(err.message));
  };

  //CART FUNCTIONS//

  //Get cart
  const getCart = async () => {
    axios
      .get(`${baseUrl}/cart`, {
        headers: {
          authorization: `Bearer ${token}`,
        },
      })
      .then((res) => setCart(res.data))
      .catch((err) => swal("getCart", err.message));
  };

  //INCREASE product quantity in cart
  const increaseQty = async (product, cartId) => {
    if (!currentUser) {
      let guestCart = JSON.parse(localStorage.getItem("guestCart"));
      //find product index in guest cart
      let index = guestCart.products
        .map((product) => product.name)
        .indexOf(product.name);
      //update product quantity
      guestCart.products[index].quantity =
        guestCart.products[index].quantity + 1;
      guestCart.products[index].productTotal =
        guestCart.products[index].productTotal + product.price;
      //recalculate cart totals
      guestCart.cartTotal = guestCart.products
        .map((product) => product.productTotal)
        .reduce((acc, curr) => acc + curr);
      guestCart.cartQuantity = guestCart.products
        .map((product) => product.quantity)
        .reduce((acc, curr) => acc + curr);
      //save guest cart to state and local storage
      setCart(guestCart);
      localStorage.setItem("guestCart", JSON.stringify(guestCart));
    } else {
      try {
        await axios
          .put(
            `${baseUrl}/cart/increase/${cartId}`,
            { product, cartId },
            {
              headers: {
                authorization: `Bearer ${token}`,
              },
            }
          )
          .then((res) => setCart(res.data), setLoading(false));
      } catch (error) {
        swal(
          "Please login or create an account to add items to your cart",
          error.message
        );
      }
    }
  };

  //DECREASE product quantity in cart
  const decreaseQty = async (product, cartId) => {
    if (!currentUser) {
      let guestCart = JSON.parse(localStorage.getItem("guestCart"));
      //find product index in guest cart
      let index = guestCart.products
        .map((product) => product.name)
        .indexOf(product.name);
      //update product quantity
      guestCart.products[index].quantity =
        guestCart.products[index].quantity - 1;
      guestCart.products[index].productTotal =
        guestCart.products[index].productTotal - product.price;
      //recalculate cart totals
      guestCart.cartTotal = guestCart.products
        .map((product) => product.productTotal)
        .reduce((acc, curr) => acc + curr);
      guestCart.cartQuantity = guestCart.products
        .map((product) => product.quantity)
        .reduce((acc, curr) => acc + curr);
      //save guest cart to state and local storage
      setCart(guestCart);
      localStorage.setItem("guestCart", JSON.stringify(guestCart));
    } else {
      try {
        await axios
          .put(
            `${baseUrl}/cart/decrease/${cartId}`,
            { product },
            {
              headers: {
                authorization: `Bearer ${token}`,
              },
            }
          )
          .then((res) => setCart(res.data))
          .catch((err) => swal(err.message));
      } catch (error) {
        swal(error.message);
      }
    }
  };

  //DELETE selected product from cart
  const deleteProduct = async (product, cartId) => {
    if (!currentUser) {
      let guestCart = JSON.parse(localStorage.getItem("guestCart"));
      //remove selected product from guest cart
      guestCart.products = guestCart.products.filter(
        (item) => item.name !== product.name
      );
      //recalculate cart totals
      guestCart.cartTotal = guestCart.products
        .map((product) => product.productTotal)
        .reduce((acc, curr) => acc + curr);
      guestCart.cartQuantity = guestCart.products
        .map((product) => product.quantity)
        .reduce((acc, curr) => acc + curr);
      //save guest cart to state and local storage
      setCart(guestCart);
      localStorage.setItem("guestCart", JSON.stringify(guestCart));
    } else {
      try {
        await axios
          .put(
            `${baseUrl}/cart/delete/${cartId}`,
            { product, cartId },
            {
              headers: {
                authorization: `Bearer ${token}`,
              },
            }
          )
          .then((res) => setCart(res.data))
          .catch((err) => swal(err.message));
      } catch (error) {
        swal(error.message);
      }
    }
  };
  useEffect(() => {
    getCategoryList();
    getProductList();
    if (!currentUser && storage === null) {
      setCart(guestCart);
      localStorage.setItem("guestCart", JSON.stringify(guestCart));
    }

    if (currentUser) {
      getCart();
    }
  }, []);
  return (
    <AppContext.Provider
      value={{
        showCheckoutModal,
        setShowCheckoutModal,
        selectedCategory,
        setSelectedCategory,
        selectedProduct,
        setSelectedProduct,
        loading,
        setLoading,
        categoryList,
        setCategoryList,
        productList,
        currentUser,
        setCurrentUser,
        getCategoryList,
        drop,
        setDrop,
        cart,
        setCart,
        token,
        increaseQty,
        decreaseQty,
        deleteProduct,
        purchased,
        setPurchased,
      }}
    >
      {children}
    </AppContext.Provider>
  );
};
