import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Card, ListGroup, ListGroupItem } from "react-bootstrap";
import "./ShoppingCart.scss";

const baseUrl = "http://localhost:8080/api";

export function ShoppingCart(props) {
  const [cart, setCart] = useState();
  useEffect(() => {
    const token = sessionStorage.getItem("token");
    //   // Get user cart
    axios
      .get(`${baseUrl}/cart`, {
        headers: {
          authorization: `Bearer ${token}`,
        },
      })
      .then((res) => setCart(res.data))
      .catch((err) => console.log(err.message));
    //   //delete selected product from user cart
    //   axios.put(`/cart/${cartId}`);
  }, []);
  console.log(cart);
  return (
    <section className="cart">
      <div className="cart__list">
        <h1>Items in your cart</h1>
        {cart?.products.map((product) => {
          return (
            //  ADJUST FONT SIZES FOR BREAKPOINTS
            <Link to={`/product/${product.productId}`}>
              <Card className="cart__card">
                <Card.Img
                  className="cart__card--image"
                  variant="top"
                  src={product.image}
                />
                <Card.Body>
                  <Card.Title>{product.name}</Card.Title>
                  <Link to="/categories">
                    <Card.Text>{`${product.category}`}</Card.Text>
                  </Link>
                </Card.Body>
                <div className="cart__info">
                  <ListGroupItem>{`Price: $${product.price.toFixed(
                    2
                  )}`}</ListGroupItem>
                  <ListGroupItem>Dapibus ac facilisis in</ListGroupItem>
                  <ListGroupItem>Vestibulum at eros</ListGroupItem>
                </div>
              </Card>
            </Link>
          );
        })}
      </div>
      <div className="cart__total">
        <h1>Your total</h1>
        <h1>{`$${cart?.cartTotal.toFixed(2)}`}</h1>
      </div>
    </section>
  );
}

export default ShoppingCart;
