import React, { createContext, useState } from "react";
import axios from "axios";

const baseUrl = "http://localhost:8080/api";

export const AppContext = createContext();

export const ContextProvider = (props) => {
  const token = sessionStorage.getItem("token");
  const [selectedCategory, setSelectedCategory] = useState([]);
  const [categoryList, setCategoryList] = useState([]);
  const [user, setUser] = useState("Guest");
  const [drop, setDrop] = useState("");
  const [cart, setCart] = useState();
  const [selectedProduct, setSelectedProduct] = useState([]);

  const getCategoryList = async (id) => {
    const { data } = axios.get(`${baseUrl}/categories`);
    setCategoryList(data);
    const selectedCat = data.filter((cat) => cat._id === id);
    const selectedCatName = selectedCat[0].name;
    setDrop(selectedCatName);
  };

  const getSelectedCategory = async (id) => {
    const { data } = axios.get(`${baseUrl}/categories/${id}`);
    setSelectedCategory(data);
  };

  const getCart = async () => {
    axios
      .get(`${baseUrl}/cart`, {
        headers: {
          authorization: `Bearer ${token}`,
        },
      })
      .then((res) => setCart(res.data))
      .catch((err) => alert(err.message));
  };
  return (
    <AppContext.Provider value={"hello"}>{props.children}</AppContext.Provider>
  );
};
