import React, { useState } from "react";
import "./Carousel.scss";
import { Carousel } from "react-bootstrap";
import bmx from "../../assets/images/rideBMX.jpg";
import fixie from "../../assets/images/fixie.jpeg";
import offRoad from "../../assets/images/offroad.jpg";

function ControlledCarousel() {
  const [index, setIndex] = useState(0);

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };

  return (
    <Carousel className="hero" activeIndex={index} onSelect={handleSelect}>
      <Carousel.Item>
        <img className="d-block w-100 hero__img" src={bmx} alt="First slide" />
        <Carousel.Caption>
          <h3 className="hero__title">BMX</h3>
          <p className="hero__text">Browse our entire range of BMX bikes</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100 hero__img"
          src={fixie}
          alt="Second slide"
        />

        <Carousel.Caption>
          <h3 className="hero__title">Second slide label</h3>
          <p className="hero__text">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit.
          </p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100 hero__img"
          src={offRoad}
          alt="Third slide"
        />

        <Carousel.Caption>
          <h3 className="hero__title">Third slide label</h3>
          <p className="hero__text">
            Praesent commodo cursus magna, vel scelerisque nisl consectetur.
          </p>
        </Carousel.Caption>
      </Carousel.Item>
    </Carousel>
  );
}

export default ControlledCarousel;
