import React, { useContext, useEffect, useState } from "react";
import ShoppingCartRoundedIcon from "@material-ui/icons/ShoppingCartRounded";
import LoginModal from "../LoginModal/LogInModal";
import SignUpModal from "../SignUpModal/SignUpModal";
import "./Header.scss";
import { Navbar, Nav, NavDropdown } from "react-bootstrap";
import { AppContext } from "../../AppContext/AppContext";

function Header() {
  const { currentUser, setCurrentUser, categoryList } = useContext(AppContext);
  const [logInModalShow, setLogInModalShow] = useState(false);
  const [signUpModalShow, setSignUpModalShow] = useState(false);
  // const [user, setUser] = useState("Guest");

  useEffect(() => {
    const user = sessionStorage.getItem("user");
    console.log();
    setCurrentUser(user);
  }, [setCurrentUser]);

  //LogIn Modal Switches
  const handleLoginModalOpen = () => {
    setLogInModalShow(true);
  };
  const handleLoginModalClose = () => {
    setLogInModalShow(false);
  };
  const handleLoginModalSwitch = () => {
    setLogInModalShow(false);
    setSignUpModalShow(true);
  };

  //SignUp Modal Show
  const handleSignupModalOpen = () => {
    setSignUpModalShow(true);
  };
  const handleSignupModalClose = () => {
    setSignUpModalShow(false);
  };
  const handleSignupModalSwitch = () => {
    setSignUpModalShow(false);
    setLogInModalShow(true);
  };

  //Sign user out by removing token and user from session storage
  const handleSignOut = () => {
    sessionStorage.removeItem("token");
    sessionStorage.removeItem("user");
    setCurrentUser(null);
  };
  console.log(currentUser);
  console.log(categoryList);
  return (
    <nav className="nav">
      <Navbar className="nav__color" variant="dark" expand="lg">
        <Nav.Link className="nav__brand" href="/">
          eShop
        </Nav.Link>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="/cart">
              <ShoppingCartRoundedIcon className="nav__cart" />
            </Nav.Link>
            <NavDropdown title={currentUser || "Guest"} id="basic-nav-dropdown">
              {currentUser ? (
                <>
                  <NavDropdown.Item className="nav__link" href="/categories">
                    Shop Categories
                  </NavDropdown.Item>
                  {categoryList.map((cat) => {
                    return (
                      <NavDropdown.Item
                        className="nav__link"
                        href="/categories"
                      >
                        Shop Categories
                      </NavDropdown.Item>
                    );
                  })}
                  <NavDropdown.Divider />
                  <NavDropdown.Item
                    className="nav__link"
                    onClick={handleSignOut}
                  >
                    Sign Out
                  </NavDropdown.Item>
                </>
              ) : (
                <>
                  <NavDropdown.Item onClick={handleLoginModalOpen}>
                    Log In
                  </NavDropdown.Item>
                  <NavDropdown.Item
                    className="nav__link"
                    onClick={handleSignupModalOpen}
                  >
                    Create Account
                  </NavDropdown.Item>
                </>
              )}
            </NavDropdown>

            {/* <NavDropdown title="Shop" id="basic-nav-dropdown ">
              <NavDropdown.Item className="nav__link" href="/categories">
                Categories
              </NavDropdown.Item>
              <NavDropdown.Item className="nav__link" href="/cart">
                Cart
              </NavDropdown.Item>
              {!currentUser ? (
                <NavDropdown.Item
                  className="nav__link"
                  onClick={handleSignupModalOpen}
                >
                  Create Account
                </NavDropdown.Item>
              ) : (
                <NavDropdown.Item className="nav__link" onClick={handleSignOut}>
                  Sign Out
                </NavDropdown.Item>
              )}

              <NavDropdown.Divider />
              <NavDropdown.Item className="nav__link" href="#return-policy">
                Return Policy
              </NavDropdown.Item>
              <NavDropdown.Item className="nav__link" href="#contact-us">
                Contact Us
              </NavDropdown.Item>
            </NavDropdown> */}
          </Nav>
        </Navbar.Collapse>
        {/* <Navbar.Brand className="nav__brand" href="/">
          eShop
        </Navbar.Brand> */}
        <LoginModal
          onHide={handleLoginModalClose}
          show={logInModalShow}
          loginClose={handleLoginModalClose}
          loginSwitch={handleLoginModalSwitch}
        />
        <SignUpModal
          onHide={handleSignupModalClose}
          show={signUpModalShow}
          showLogin={logInModalShow}
          signupClose={handleSignupModalClose}
          signupSwitch={handleSignupModalSwitch}
        />
      </Navbar>
    </nav>
  );
}

export default Header;
