import React, { Component } from "react";
import axios from "axios";
import "./Login.scss";

export default class Login extends Component {
  state = {
    formData: null,
  };

  handleChange = (e) => {
    //grab form data and set it to state
    this.setState({
      formData: { ...this.state.formData, [e.target.name]: e.target.value },
    });
  };

  handleSubmit = (e) => {
    // submit it to backend to receive token
    e.preventDefault();
    axios.post("/api/users", this.state.formData).then((res) => {
      //get token from response
      const token = res.data.token;
      //save token in session storage
      sessionStorage.setItem("token", token);
      //navigate user to home page
      this.props.history.push("/");
    });
  };

  render() {
    return (
      <section className="login">
        <div className="login__card">
          <div className="login__welcome">
            <h1 className="login__welcome--title">Login</h1>
          </div>
          <form className="login__form" onSubmit={this.handleSubmit}>
            <label>Email</label>
            <input type="email" name="email" />
            <label>Password</label>
            <input type="password" name="password" />
            <button>Login</button>
          </form>
          {/* <h5>if alredy registered log in here</h5> */}
        </div>
      </section>
    );
  }
}
