import React, { useEffect, useState } from "react";
import LoginModal from "../LoginModal/LogInModal";
import "./Header.scss";
import {
  Navbar,
  Nav,
  NavDropdown,
  OverlayTrigger,
  Popover,
  Button,
} from "react-bootstrap";

function Header() {
  const [modalShow, setModalShow] = useState(false);
  const [user, setUser] = useState("Guest");

  const popover = (
    <Popover id="popover-basic">
      <Popover.Title as="h3">Popover right</Popover.Title>
      <Popover.Content>
        And here's some <strong>amazing</strong> content. It's very engaging.
        right?
      </Popover.Content>
    </Popover>
  );

  useEffect(() => {
    const user = sessionStorage.getItem("user");
    setUser(user);
  }, []);

  const handleModalOpen = () => {
    setModalShow(true);
  };
  const handleModalClose = () => {
    setModalShow(false);
  };

  return (
    <Navbar className="nav__color" variant="dark" expand="md">
      {/* <Navbar.Brand className="nav__brand" href="/">
        eShop
      </Navbar.Brand> */}
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="mr-auto">
          <Nav.Link href="/">Home</Nav.Link>
          <Nav.Link onClick={handleModalOpen}>
            {!user ? "Guest" : user}
          </Nav.Link>
          <NavDropdown title="Shop" id="basic-nav-dropdown">
            <NavDropdown.Item className="nav__link" href="/categories">
              Categories
            </NavDropdown.Item>
            <NavDropdown.Item className="nav__link" href="/signup">
              Sign Up
            </NavDropdown.Item>
            <NavDropdown.Item className="nav__link" href="/cart">
              Cart #
            </NavDropdown.Item>
            <NavDropdown.Divider />
            <NavDropdown.Item className="nav__link" href="#return-policy">
              Return Policy
            </NavDropdown.Item>
            <NavDropdown.Item className="nav__link" href="#contact-us">
              Contact Us
            </NavDropdown.Item>
          </NavDropdown>
        </Nav>
      </Navbar.Collapse>
      <Navbar.Brand className="nav__brand" href="/">
        eShop
      </Navbar.Brand>
      <DropdownButton
        menuAlign="right"
        title="Dropdown right"
        id="dropdown-menu-align-right"
      >
        <Dropdown.Item eventKey="1">Action</Dropdown.Item>
        <Dropdown.Item eventKey="2">Another action</Dropdown.Item>
        <Dropdown.Item eventKey="3">Something else here</Dropdown.Item>
        <Dropdown.Divider />
        <Dropdown.Item eventKey="4">Separated link</Dropdown.Item>
      </DropdownButton>
      <LoginModal
        onHide={handleModalClose}
        show={modalShow}
        close={handleModalClose}
      />
    </Navbar>
  );
}

export default Header;
