import React, { Component } from "react";
import { Link } from "react-router-dom";
import "./SignUp.scss";
import axios from "axios";
class SignUp extends Component {
  state = {
    formData: null,
  };

  //With hooks
  //const [formData, setFormData] = useState(null);
  //   const handlechange = e => {
  //       setformData({...formData, [e.target.name]: e.target.value})
  //   }

  handleChange = (e) => {
    //grab form data and set it to state
    this.setState({
      formData: { ...this.state.formData, [e.target.name]: e.target.value },
    });
  };

  handleSubmit = (e) => {
    // submit it to backend to receive token
    e.preventDefault();
    axios.post("/api/users", this.state.formData).then((res) => {
      //get token from response
      const token = res.data.token;
      //save token in session storage
      sessionStorage.setItem("token", token);
      //navigate user to home page
      this.props.history.push("/");
    });
  };

  render() {
    return (
      // <div>
      //   <h1>Login</h1>
      //   <form onSubmit={this.handleSubmit}>
      //     <label>Email</label>
      //     <input type="email" name="email" onChange={this.handleChange} />
      //     <label>Password</label>
      //     <input type="password" name="password" onChange={this.handleChange} />
      //     <button>Login</button>
      //   </form>
      // </div>
      <section className="signUp">
        <div className="signUp__card">
          <div className="signUp__welcome">
            <h1 className="signUp__welcome--title">Sign Up</h1>
          </div>
          <form className="signUp__form" onSubmit={this.handleSubmit}>
            <div className="signUp__form--card">
              <label className="signUp__form--label">Email</label>
              <input
                className="signUp__form--input email"
                type="email"
                name="email"
              />
              <label className="signUp__form--label">Password</label>
              <input
                className="signUp__form--input password"
                type="password"
                name="password"
              />
              <button className="signUp__form--button">Sign Up</button>
            </div>
          </form>
          <h5 className="signUp__redirect">
            if already registered.
            <Link to="/login"> Log In </Link>
            here
          </h5>
        </div>
      </section>
    );
  }
}
export default SignUp;
