import React, { useState } from "react";
import { Modal } from "react-bootstrap";
import axios from "axios";

function AlertModal({ alert, setAlert, close }) {
  const [formData, setFormData] = useState();

  const handleChange = (e) => {
    //get form data and set it to state
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    // submit it to backend to receive token
    e.preventDefault();
    axios
      .post("http://localhost:8080/api/users/login", formData)
      .then((res) => {
        //get token from response
        const token = res.data.token;
        //get user from response
        const { firstName, lastName, _id } = res.data.user;
        console.log(res.data.user);
        console.log(_id);
        const user = firstName + " " + lastName;
        //save token in session storage
        sessionStorage.setItem("token", token);
        //save user in session storage
        sessionStorage.setItem("user", user);
        //save user id in session storage
        sessionStorage.setItem("userId", _id);
        //navigate user to home page
        // this.props.history.push("/");
      })
      .catch((err) => alert(err.message));
  };

  return (
    <>
      <Modal
        size="sm"
        show={alert}
        aria-labelledby="example-modal-sizes-title-sm"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-modal-sizes-title-sm">
            Please Log In
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="login__card">
            <div className="login__welcome">
              <h1 className="login__welcome--title">Log In</h1>
              <button className="login__welcome--close" onClick={close}>
                X
              </button>
            </div>
            <form className="login__form" onSubmit={handleSubmit}>
              <div className="login__form--card">
                <label className="login__form--label">Email</label>
                <input
                  className="login__form--input email"
                  onChange={handleChange}
                  type="email"
                  name="email"
                />
                <label className="login__form--label">Password</label>
                <input
                  className="login__form--input password"
                  onChange={handleChange}
                  type="password"
                  name="password"
                />
                <button className="login__form--button" onClick={close}>
                  Login
                </button>
              </div>
            </form>
            {/* <h5 onClick={props.loginSwitch} className="login__redirect">
              Not a member?
              <Link> Sign Up </Link>
              here
            </h5> */}
          </div>
        </Modal.Body>
      </Modal>
    </>
  );
}

export default AlertModal;
