import axios from "axios";
import React, { Component } from "react";
import "./OnSale.scss";
import {
  Nav,
  Form,
  FormControl,
  Button,
  Card,
  CardDeck,
} from "react-bootstrap";

const baseUrl = "http://localhost:8080/api";

class OnSale extends Component {
  state = {
    selectedProduct: [],
  };
  componentDidMount() {
    const { id } = this.props.match.params;
    axios.get(`${baseUrl}/product/${id}`).then((res) => {
      this.setState({ selectedProduct: res.data });
    });
  }

  render() {
    console.log(this.state.selectedProduct.image);
    const selectedProduct = this.state;
    return (
      <section className="product">
        <img
          className="product__img"
          alt="product img"
          src={selectedProduct.image}
        ></img>
        <div className="product__details">
          <div className="product__details--info">
            <h1>{selectedProduct.name}</h1>
            <h2>{selectedProduct.category}</h2>
          </div>
          <div className="product__details--action">
            <h3>QTY</h3>
            <h3>ADD TO CART</h3>
          </div>
          <div className="product__details--footer">
            <h3>Product description</h3>
          </div>
          <div className="product__review">
            <div className="product__review--card">
              <div className="product__review--head">
                <h3>user</h3>
                <h3>timestamp</h3>
              </div>
              <h3>review text goes here</h3>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default OnSale;
