import axios from "axios";
import { Form, Button } from "react-bootstrap";
import React, { useEffect, useState, useContext } from "react";
import { AppContext } from "../../AppContext/AppContext";
import { useHistory } from "react-router-dom";
import swal from "sweetalert";
import "./ProductDetail.scss";

const baseUrl = "http://localhost:8080/api";

function OnSale(props) {
  const [selectedProduct, setSelectedProduct] = useState([]);
  const [quantity, setQuantity] = useState(1);
  const { setCart, cart, token, currentUser } = useContext(AppContext);
  const history = useHistory();

  // const stateCart = {
  //   products: [],
  //   cartQuantity: 0,
  //   cartTotal: 0,
  // };
  // if (!currentUser) {
  //   setCart([...stateCart]);
  // }

  //Get from session storage
  // const token = sessionStorage.getItem("token");
  // const user = sessionStorage.getItem("user");
  // const userId = sessionStorage.getItem("userId");

  const handleChange = (e) => {
    setQuantity(e.target.value);
  };

  const handleSubmit = (product) => {
    const price = product.price.slice(1, 9).split(",").join("");
    console.log("price", price);
    // console.log("product", product);
    console.log("cart", cart);
    // const cartQuantity = cart.products
    //   .map((product) => product.quantity)
    //   .reduce((acc, curr) => acc + curr);
    // console.log(cartQuantity);
    //if not current user
    if (!currentUser) {
      const guestCart = sessionStorage.getItem("cart");
      console.log("guestCart", guestCart);
      const isExist = cart.products.includes(product);
      console.log("inside", isExist);
      if (!isExist) {
        console.log("dosent exist");
        // let newProduct = {
        //   quantity,
        //   productTotal:

        // }
        cart.products.push(product);
        console.log("updated cart", cart);
      } else {
        const index = cart.products.indexOf(product);
        console.log("does Exist", index);

        const productPrice = product.price;
        const productTotal = cart.products[index].productTotal;
        const productQty = cart.products[index].quantity;
        console.log("prodPrice", productPrice);
        console.log("prodTotal", productTotal);
        console.log("prodQty", productQty);
      }
    }
    //create cart through state
    //if current user allow access to user cart and update

    // if (currentUser) {
    // axios
    //   .post(
    //     `${baseUrl}/cart`,
    //     {
    //       quantity,
    //       product,
    //       price,
    //     },
    //     {
    //       headers: {
    //         authorization: `Bearer ${token}`,
    //       },
    //     }
    //   )
    //   .then((res) => {
    //     setCart(res.data);
    //     swal("Success!", "Item has beeen added to cart");
    //     history.push("/cart");
    //   })
    //   .catch((err) => swal("Please sign in to continue shopping"));
    // } else {
    // }
  };

  useEffect(() => {
    const { id } = props.match.params;
    axios.get(`${baseUrl}/product/${id}`).then((res) => {
      const selectedProduct = res.data;
      setSelectedProduct(selectedProduct);
    });
  }, [props.match.params]);
  console.log("cart", cart);
  return (
    <>
      <div className="product">
        <img
          className="product__img"
          alt="product img"
          src={selectedProduct.image}
        ></img>
        <div className="product__details">
          <div className="product__details--top">
            <div className="product__details--info">
              <h1 className="product__details--title">
                {selectedProduct.name}
              </h1>
              <h3 className="product__details--category">
                {selectedProduct.category}
              </h3>
              <h4>Price: {selectedProduct.price}</h4>
            </div>
            <div className="product__details--action">
              <div className="product__details--qty">
                <Form>
                  <Form.Group controlId="exampleForm.ControlSelect1">
                    <Form.Label>QTY</Form.Label>
                    <Form.Control onChange={handleChange} as="select">
                      <option>1</option>
                      <option>2</option>
                      <option>3</option>
                      <option>4</option>
                      <option>5</option>
                    </Form.Control>
                  </Form.Group>
                </Form>
              </div>
              <Button
                onClick={() => {
                  handleSubmit(selectedProduct);
                }}
                className="product__details--add"
                variant="outline-dark"
              >
                ADD TO CART
              </Button>
            </div>
          </div>
          <div className="product__details--footer">
            <p>{selectedProduct.description}</p>
          </div>
          {/* <div className="product__review">
              <div className="product__review--card">
                <div className="product__review--head">
                  <h3>user</h3>
                  <h3>timestamp</h3>
                </div>
                <h3>review text goes here</h3>
              </div>
            </div> */}
        </div>
      </div>
    </>
  );
}

export default OnSale;
